class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comment-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let s=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(s),t.deleteComment($(" .delete-comment-button",s)),new ToggleLike($(" .toggle-like-button",s)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}newCommentDom(e){return $(`<li id="comment-${e._id}">\n        <div class="comment-content">\n     \n          <a class="delete-comment-button" href="/comments/destroy/${e._id}">\n            <i class="fa-solid fa-trash-can fa-lg"></i>\n          </a>\n          <div class="post-img-vau"> \n                 <a href="/users/profile/${e.user._id}">\n                 ${e.user.avatar?`<img src="${e.user.avatar}" alt="${e.user.name}">`:` <img\n            class="rounded-circle"\n            id="user-dp"\n            src="https://images.assetsdelivery.com/compings_v2/tanyastock/tanyastock1608/tanyastock160801788.jpg"\n            alt="${e.user.name}"\n            />`}\n                 </a>\n          </div>\n                <a href="/users/profile/${e.user._id}">\n          <small class="comment-name"> ${e.user.name} </small>\n\n                 </a>\n          <div class="main-comment-content">${e.content}</div>\n          <br />\n\n          <small>\n            <a\n              class="toggle-like-button"\n              data-likes="${e.likes.length}"\n              href="/likes/toggle/?id=${e._id}&type=Comment"\n            >\n              <i class="fa-regular white fa-heart fa-lg">  ${e.likes.length} </i>\n            </a>\n       \n          </small>\n        </div>\n      </li>\n      `)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}}