class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click(function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done(function(t){let a=parseInt($(e).attr("data-likes"));console.log(a),1==t.data.deleted?(a-=1,$(e).attr("data-likes",a),$(e).html(`<i class="fa-regular white  fa-heart fa-lg"> ${a} </i>`)):(a+=1,$(e).attr("data-likes",a),$(e).html(`<i class="fa-solid  pink fa-heart fa-lg"> ${a} </i>`))}).fail(function(t){console.log("error in completing the request")})})}}